import React from 'react'

const Square = (props) => {
  console.log(props.squares)
  return (
  <>

  <div id="grid" >
   {props.squares.map((value,index, )=> {
    return(
      <div className="square" 
      key={index}
      id={index}
      onClick={() => {props.handleClick(index)}}>
      {value}
      </div>
    )
   })}
</div>
</>
)
}


export default Square
